import {isAlphabetical} from 'is-alphabetical'
import {isDecimal} from 'is-decimal'

export function isAlphanumerical(character) {
  const code = typeof character === 'string' ? character.charCodeAt(0) : character;

  // Check if the character code falls within the range of alphanumeric characters in ASCII
  return (code >= 48 && code <= 57) || // 0-9
         (code >= 65 && code <= 90) || // A-Z
         (code >= 97 && code <= 122);  // a-z
}
